fun MainContent(modifier: Modifier = Modifier) {
 // List of available topics/screens - easily extensible
 val menuItems = remember {
 listOf(
 MenuItem(
 id = "parallel_timers",
 title = "Parallel Timers",
 description = "Demonstration of parallel timer execution",
 screen = { ParallelTimers() }
 )
 // Add more menu items here as you build more screens
 // MenuItem(
 // id = "another_topic",
 // title = "Another Topic",
 // description = "Description of another topic",
 // screen = { AnotherScreen() }
 // )
 )
 }

 // Navigation state - null means show menu, otherwise show selected screen
 var selectedMenuItem by remember { mutableStateOf<MenuItem?>(null) }

 Column(modifier = modifier.fillMaxSize()) {
 // Top bar
 if (selectedMenuItem != null) {
 TopAppBar(
 title = { Text(selectedMenuItem!!.title) },
 navigationIcon = {
 IconButton(onClick = { selectedMenuItem = null }) {
 Icon(
 imageVector = Icons.Default.ArrowBack,
 contentDescription = "Back to menu"
 )
 }
 }
 )
 } else {
 TopAppBar(
 title = { Text("Concurrency Demos") }
 )
 }

 // Content area
 selectedMenuItem?.let {
 it.screen()
 }?: run {
 MenuList(
 menuItems = menuItems,
 onItemClick = { selectedMenuItem = it },
 modifier = Modifier.fillMaxSize()
 )
 }
 }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun MenuList(
 menuItems: List<MenuItem>,
 onItemClick: (MenuItem) -> Unit,
 modifier: Modifier = Modifier
) {
 LazyColumn(
 modifier = modifier.padding(16.dp),
 verticalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 items(menuItems) { item ->
 Card(
 onClick = { onItemClick(item) },
 modifier = Modifier.fillMaxWidth(),
 elevation = CardDefaults.cardElevation(defaultElevation = 4.dp)
 ) {
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 ) {
 Text(
 text = item.title,
